import{_ as a,c as s,o as e,a as n}from"./app.9abf584e.js";const C=JSON.parse('{"title":"Developing on GNU/Linux","description":"","frontmatter":{},"headers":[{"level":2,"title":"Using xmake","slug":"using-xmake","link":"#using-xmake","children":[{"level":3,"title":"Step 1: Install xmake and Qt 5","slug":"step-1-install-xmake-and-qt-5","link":"#step-1-install-xmake-and-qt-5","children":[]},{"level":3,"title":"Step 2: Compile","slug":"step-2-compile","link":"#step-2-compile","children":[]},{"level":3,"title":"Step 3: Run unit test","slug":"step-3-run-unit-test","link":"#step-3-run-unit-test","children":[]},{"level":3,"title":"Step 4: Install to build/package","slug":"step-4-install-to-build-package","link":"#step-4-install-to-build-package","children":[]},{"level":3,"title":"Step 5: Launch Mogan Editor","slug":"step-5-launch-mogan-editor","link":"#step-5-launch-mogan-editor","children":[]}]},{"level":2,"title":"Using cmake","slug":"using-cmake","link":"#using-cmake","children":[]}],"relativePath":"guide/Develop_on_Linux.md","lastUpdated":null}'),l={name:"guide/Develop_on_Linux.md"},t=n(`<h1 id="developing-on-gnu-linux" tabindex="-1">Developing on GNU/Linux <a class="header-anchor" href="#developing-on-gnu-linux" aria-hidden="true">#</a></h1><h2 id="using-xmake" tabindex="-1">Using xmake <a class="header-anchor" href="#using-xmake" aria-hidden="true">#</a></h2><h3 id="step-1-install-xmake-and-qt-5" tabindex="-1">Step 1: Install xmake and Qt 5 <a class="header-anchor" href="#step-1-install-xmake-and-qt-5" aria-hidden="true">#</a></h3><p>On Debian or Debian derivatives:</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">sudo add-apt-repository ppa:xmake-io/xmake</span></span>
<span class="line"><span style="color:#A6ACCD;">sudo apt update</span></span>
<span class="line"><span style="color:#A6ACCD;">sudo apt upgrade --yes gcc</span></span>
<span class="line"><span style="color:#A6ACCD;">sudo apt install --yes qtbase5-dev libqt5svg5-dev xmake</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>Sometimes, we need the latest xrepo:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">xrepo update-repo</span></span>
<span class="line"></span></code></pre></div><h3 id="step-2-compile" tabindex="-1">Step 2: Compile <a class="header-anchor" href="#step-2-compile" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">xmake</span></span>
<span class="line"></span></code></pre></div><h3 id="step-3-run-unit-test" tabindex="-1">Step 3: Run unit test <a class="header-anchor" href="#step-3-run-unit-test" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">TEXMACS_PATH=</span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">PWD/TeXmacs xmake run --yes --verbose --diagnosis --group=tests</span></span>
<span class="line"></span></code></pre></div><h3 id="step-4-install-to-build-package" tabindex="-1">Step 4: Install to <code>build/package</code> <a class="header-anchor" href="#step-4-install-to-build-package" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">xmake install -o build/package mogan_install</span></span>
<span class="line"></span></code></pre></div><h3 id="step-5-launch-mogan-editor" tabindex="-1">Step 5: Launch Mogan Editor <a class="header-anchor" href="#step-5-launch-mogan-editor" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">build/package/bin/mogan</span></span>
<span class="line"></span></code></pre></div><h2 id="using-cmake" tabindex="-1">Using cmake <a class="header-anchor" href="#using-cmake" aria-hidden="true">#</a></h2><p>Assuming you are in the project root of mogan:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">mkdir build </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">cd</span><span style="color:#A6ACCD;"> build</span></span>
<span class="line"><span style="color:#A6ACCD;">cmake .. -DCMAKE_INSTALL_PREFIX=</span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">HOME/software</span></span>
<span class="line"><span style="color:#A6ACCD;">make install -j12</span></span>
<span class="line"></span></code></pre></div><p>Now, you can launch Mogan via:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">HOME/software/bin/mogan.sh</span></span>
<span class="line"></span></code></pre></div><p>Using <code>make install</code> and developing in a separated folder could help you setup a clean environment. If you are familiar with the source code, you can change the TEXMACS_PATH to develop on the Scheme part without having to build the C++ part.</p>`,21),p=[t];function i(o,c,d,r,u,h){return e(),s("div",null,p)}const m=a(l,[["render",i]]);export{C as __pageData,m as default};
