import{_ as a,c as s,o as e,a as n}from"./app.9abf584e.js";const g=JSON.parse('{"title":"Developing on macOS","description":"","frontmatter":{},"headers":[{"level":2,"title":"Using xmake","slug":"using-xmake","link":"#using-xmake","children":[{"level":3,"title":"Step 1: Install xmake and xrepo","slug":"step-1-install-xmake-and-xrepo","link":"#step-1-install-xmake-and-xrepo","children":[]},{"level":3,"title":"Step 2: Compile","slug":"step-2-compile","link":"#step-2-compile","children":[]},{"level":3,"title":"Step 3: Run unit test","slug":"step-3-run-unit-test","link":"#step-3-run-unit-test","children":[]},{"level":3,"title":"Step 4: Install to build/macosx/{arch}/release/Mogan.app/","slug":"step-4-install-to-build-macosx-arch-release-mogan-app","link":"#step-4-install-to-build-macosx-arch-release-mogan-app","children":[]},{"level":3,"title":"Step 5: Launch Mogan Editor","slug":"step-5-launch-mogan-editor","link":"#step-5-launch-mogan-editor","children":[]}]},{"level":2,"title":"Using cmake","slug":"using-cmake","link":"#using-cmake","children":[]}],"relativePath":"guide/Develop_on_macOS.md","lastUpdated":null}'),l={name:"guide/Develop_on_macOS.md"},p=n(`<h1 id="developing-on-macos" tabindex="-1">Developing on macOS <a class="header-anchor" href="#developing-on-macos" aria-hidden="true">#</a></h1><h2 id="using-xmake" tabindex="-1">Using xmake <a class="header-anchor" href="#using-xmake" aria-hidden="true">#</a></h2><h3 id="step-1-install-xmake-and-xrepo" tabindex="-1">Step 1: Install xmake and xrepo <a class="header-anchor" href="#step-1-install-xmake-and-xrepo" aria-hidden="true">#</a></h3><p>For Homebrew:</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">brew install xmake qt@5 ccache</span></span>
<span class="line"><span style="color:#A6ACCD;">brew link qt@5</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>Sometimes, we need the latest xrepo:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">xrepo update-repo</span></span>
<span class="line"></span></code></pre></div><h3 id="step-2-compile" tabindex="-1">Step 2: Compile <a class="header-anchor" href="#step-2-compile" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">xmake</span></span>
<span class="line"></span></code></pre></div><h3 id="step-3-run-unit-test" tabindex="-1">Step 3: Run unit test <a class="header-anchor" href="#step-3-run-unit-test" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">TEXMACS_PATH=</span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">PWD/TeXmacs xmake run --yes --verbose --diagnosis --group=tests</span></span>
<span class="line"></span></code></pre></div><h3 id="step-4-install-to-build-macosx-arch-release-mogan-app" tabindex="-1">Step 4: Install to <code>build/macosx/{arch}/release/Mogan.app/</code> <a class="header-anchor" href="#step-4-install-to-build-macosx-arch-release-mogan-app" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#676E95;"># x86_64</span></span>
<span class="line"><span style="color:#A6ACCD;">xmake install -o build/macosx/x86_64/release/Mogan.app/Contents/Resources/ mogan_install</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># arm64</span></span>
<span class="line"><span style="color:#A6ACCD;">xmake install -o build/macosx/arm64/release/Mogan.app/Contents/Resources/ mogan_install</span></span>
<span class="line"></span></code></pre></div><h3 id="step-5-launch-mogan-editor" tabindex="-1">Step 5: Launch Mogan Editor <a class="header-anchor" href="#step-5-launch-mogan-editor" aria-hidden="true">#</a></h3><p>Sometimes (for macOS M1), we need to codesign it first</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">codesign --force --deep --sign - ./build/macosx/arm64/release/Mogan.app</span></span>
<span class="line"></span></code></pre></div><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#676E95;"># x86_64</span></span>
<span class="line"><span style="color:#A6ACCD;">./build/macosx/x86_64/release/Mogan.app/Contents/MacOS/Mogan</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;"># arm64</span></span>
<span class="line"><span style="color:#A6ACCD;">./build/macosx/arm64/release/Mogan.app/Contents/MacOS/Mogan</span></span>
<span class="line"></span></code></pre></div><h2 id="using-cmake" tabindex="-1">Using cmake <a class="header-anchor" href="#using-cmake" aria-hidden="true">#</a></h2><p>Assuming you are in the project root of mogan:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">mkdir build </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">cd</span><span style="color:#A6ACCD;"> build</span></span>
<span class="line"><span style="color:#A6ACCD;">cmake .. -DCMAKE_INSTALL_PREFIX=</span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">HOME/software</span></span>
<span class="line"><span style="color:#A6ACCD;">make install -j12</span></span>
<span class="line"></span></code></pre></div><p>Now, you can launch Mogan via:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">HOME/software/bin/mogan.sh</span></span>
<span class="line"></span></code></pre></div><p>Using <code>make install</code> and developing in a separated folder could help you setup a clean environment. If you are familiar with the source code, you can change the TEXMACS_PATH to develop on the Scheme part without having to build the C++ part.</p>`,23),o=[p];function t(c,i,r,d,h,u){return e(),s("div",null,o)}const C=a(l,[["render",t]]);export{g as __pageData,C as default};
