import{_ as a,c as s,o as e,V as n}from"./chunks/framework.249fa23d.js";const C=JSON.parse('{"title":"在GNU/Linux上开发","description":"","frontmatter":{},"headers":[],"relativePath":"zh/guide/Develop_on_Linux.md","filePath":"zh/guide/Develop_on_Linux.md","lastUpdated":1706584964000}'),l={name:"zh/guide/Develop_on_Linux.md"},o=n('<h1 id="在gnu-linux上开发" tabindex="-1">在GNU/Linux上开发 <a class="header-anchor" href="#在gnu-linux上开发" aria-label="Permalink to &quot;在GNU/Linux上开发&quot;">​</a></h1><h2 id="使用xmake" tabindex="-1">使用xmake <a class="header-anchor" href="#使用xmake" aria-label="Permalink to &quot;使用xmake&quot;">​</a></h2><h3 id="第一步-安装xmake和qt-5" tabindex="-1">第一步：安装xmake和Qt 5 <a class="header-anchor" href="#第一步-安装xmake和qt-5" aria-label="Permalink to &quot;第一步：安装xmake和Qt 5&quot;">​</a></h3><p>以Debian及衍生版为例：</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">sudo add-apt-repository ppa:xmake-io/xmake</span></span>\n<span class="line"><span style="color:#A6ACCD;">sudo apt install xmake</span></span>\n<span class="line"><span style="color:#A6ACCD;"></span></span>\n<span class="line"><span style="color:#A6ACCD;">sudo apt update</span></span>\n<span class="line"><span style="color:#A6ACCD;">sudo apt install --yes build-essential libfontconfig1-dev qtbase5-dev libqt5svg5-dev</span></span></code></pre></div><p>有时候需要保持xrepo是最新的，以保证使用最新的依赖的构建定义</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">xrepo update-repo</span></span></code></pre></div><h3 id="第二步-编译" tabindex="-1">第二步：编译 <a class="header-anchor" href="#第二步-编译" aria-label="Permalink to &quot;第二步：编译&quot;">​</a></h3><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">xmake config --yes</span></span>\n<span class="line"><span style="color:#A6ACCD;">xmake build research</span></span></code></pre></div><p>如果找不到Qt，那么在config的时候，可以手动指定，比如：</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">xmake config --qt=/usr/lib/`arch`-linux-gnu/qt5/</span></span></code></pre></div><p>切换到Qt 6，也只需要：</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">xmake config --qt=/usr/lib/`arch`-linux-gnu/qt6/</span></span></code></pre></div><h3 id="第三步-测试" tabindex="-1">第三步：测试 <a class="header-anchor" href="#第三步-测试" aria-label="Permalink to &quot;第三步：测试&quot;">​</a></h3><p>见<a href="./Test.html">如何测试</a>。</p><h3 id="第四步-安装到build-packages-app-mogan" tabindex="-1">第四步: 安装到<code>build/packages/app.mogan</code> <a class="header-anchor" href="#第四步-安装到build-packages-app-mogan" aria-label="Permalink to &quot;第四步: 安装到`build/packages/app.mogan`&quot;">​</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">xmake</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">research</span></span></code></pre></div><h3 id="第五步-启动墨干" tabindex="-1">第五步：启动墨干 <a class="header-anchor" href="#第五步-启动墨干" aria-label="Permalink to &quot;第五步：启动墨干&quot;">​</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">xmake</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">run</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">research</span></span></code></pre></div><h3 id="使用vscode帮助代码补全" tabindex="-1">使用VSCode帮助代码补全 <a class="header-anchor" href="#使用vscode帮助代码补全" aria-label="Permalink to &quot;使用VSCode帮助代码补全&quot;">​</a></h3><p>安装VSCode，Clangd以及VSCode的Clangd插件。 之后在mogan文件夹执行</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">xmake project -k compile_commands</span></span></code></pre></div><p>这个命令会在mogan文件夹下生成一个compile_commands.json文件，Clangd会读取它以理解项目的组织结构，从而避免找不到头文件的错误。</p>',23),t=[o];function p(i,c,r,d,h,u){return e(),s("div",null,t)}const g=a(l,[["render",p]]);export{C as __pageData,g as default};
