import{_ as t,c as e,o as a,V as r}from"./chunks/framework.249fa23d.js";const b=JSON.parse('{"title":"什么是墨干编辑器","description":"","frontmatter":{},"headers":[],"relativePath":"zh/guide/what-is-mogan.md","filePath":"zh/guide/what-is-mogan.md","lastUpdated":1706284344000}'),o={name:"zh/guide/what-is-mogan.md"},s=r('<h1 id="什么是墨干编辑器" tabindex="-1">什么是墨干编辑器 <a class="header-anchor" href="#什么是墨干编辑器" aria-label="Permalink to &quot;什么是墨干编辑器&quot;">​</a></h1><blockquote><p><strong>让我们享受探索科学与技术的乐趣！</strong></p></blockquote><p>墨干理工套件是<a href="https://gitee.com/XmacsLabs" target="_blank" rel="noreferrer">墨客实验室</a>旗下的结构化的理工套件，包含三大自由软件产品：</p><table><thead><tr><th>中文名</th><th>英文名</th><th>产品定位</th><th>状态</th></tr></thead><tbody><tr><td>墨干</td><td>Mogan Research</td><td>墨干是结构化的科技编辑器平台——GNU TeXmacs的发行版之一</td><td>成熟</td></tr><tr><td>墨码</td><td>Mogan Code</td><td>基于GNU TeXmacs内核的代码编辑器</td><td>研发中</td></tr><tr><td>墨板</td><td>Mogan Beamer</td><td>基于GNU TeXmacs内核的幻灯片产品</td><td>研发中</td></tr></tbody></table><p>GNU TeXmacs 是法国国家科学研究中心的数学家 <a href="http://www.texmacs.org/joris/main/joris.html" target="_blank" rel="noreferrer">Joris van der Hoeven</a> 从上世纪九十年代末开始研发的一款结构化编辑器，旨在利用直观易用的图形界面编写高质量的技术文档，效果可达到与 LaTeX 相同的水准。目前它的主要用户集中于大学生、中小学教师及高校科研人员。</p><h2 id="视频-a-quick-tour-of-gnu-texmacs" tabindex="-1">视频: A quick tour of GNU TeXmacs <a class="header-anchor" href="#视频-a-quick-tour-of-gnu-texmacs" aria-label="Permalink to &quot;视频: A quick tour of GNU TeXmacs&quot;">​</a></h2><p><a href="https://player.bilibili.com/player.html?aid=376713018&amp;bvid=BV1bo4y1D7wN&amp;cid=371195201&amp;page=1" target="_blank" rel="noreferrer"><img src="https://user-images.githubusercontent.com/32867606/198896005-72077867-bd0f-4223-9f87-099ec3815ba5.png" alt="A quick tour of TeXmacs-0001"></a></p><h2 id="墨干的诞生" tabindex="-1">墨干的诞生 <a class="header-anchor" href="#墨干的诞生" aria-label="Permalink to &quot;墨干的诞生&quot;">​</a></h2><p><a href="http://texmacs.org/tmweb/contribute/team-sadhen.en.html" target="_blank" rel="noreferrer">沈浪熊猫儿</a>于2022年1月30号，在 <a href="http://texmacs.org/tmweb/contribute/team-massimiliano.en.html" target="_blank" rel="noreferrer">Massimiliano Gubinelli</a> 的 S7 Scheme 分支的基础上，发布了<a href="https://gitee.com/XmacsLabs/mogan/releases/tag/v1.0.0" target="_blank" rel="noreferrer">墨干编辑器v1.0.0</a>，并创建了<a href="https://gitee.com/XmacsLabs" target="_blank" rel="noreferrer">墨客实验室</a>这样一个非盈利组织，旨在普及GNU TeXmacs，让所有人都能够通过 Joris van der Hoeven 创造的软件享受探索科学与技术的乐趣。</p>',9),n=[s];function i(d,c,h,m,l,p){return a(),e("div",null,n)}const u=t(o,[["render",i]]);export{b as __pageData,u as default};
